import React,{useState} from 'react'
import db from '../firebase';
import './NavSidebar.css'
export default function 
() {
  return (
    <div>
        <nav className="navbar sticky-top bg-dark">
            <div className="container-fluid">
                <h6 className="navbar-brand text-primary" href="#">Salary Management App</h6>
            </div>
            <div class="btn-group dropdown">
                <button type="button" className="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" data-bs-display="static" aria-expanded="false">Account</button>
                <ul className="dropdown-menu ">
                    <li><a className="dropdown-item" href="#">Acount Settings</a></li>
                    <li><a className="dropdown-item" href="#">Logout</a></li>
                </ul>
         </div>
        </nav>
        <div className="sidebar bg-dark">
          <div className="options">
              <button className="side-item" onClick={retreieveTeachers()}>Teaching Staffs</button>
              <button className="side-item">Non-Teaching Staffs</button>
              <button className="side-item"> Generate Report</button>
          </div>
              
        </div>
    </div>
  )
}

function retreieveTeachers(){
    const [info , setInfo] = useState([]);
    
    // Start the fetch operation as soon as
    // the page loads
    window.addEventListener('load', () => {
        Fetchdata();
      });

    // Fetch the required data using the get() method
    const Fetchdata = ()=>{
        db.collection("employees").get().then((querySnapshot) => {
            
            // Loop through the data and store
            // it in array to display
            querySnapshot.forEach(element => {
                var data = element.data();
                setInfo(arr => [...arr , data]);
                  
            });
        })
    }
      
    // Display the result on the page
    return (
        <div>
            <center>
            <h2>Student Details</h2>
            </center>
          
        {
            info.map((data) => (
            <Frame course={data.CourseEnrolled} 
                  name={data.Nane} 
                  age={data.Age}/>
            ))
        }
        </div>
    )
}
