import React,{useState, useEffect, Component} from 'react'
import db from './../firebase';
import {collection, query, getDocs} from 'firebase/firestore';
import './Teaching.css';
import ReactDOM  from 'react-dom';

function fetchDepartment(){
  let dept_id=[];
  const tch_dept=query(collection(db,'teaching_department'));
  const query = useFirestoreQuery(["products"],   tch_dept);
  if (query.isLoading) {
    return dept_id;
  }

  const snapshot = query.data;

  return snapshot.docs.map((docSnapshot) => {
    dept_id.push(docSnapshot.id);
    return dept_id;
  });
}



export default function Teaching() {
  let dept_id=[];
  useEffect(()=>{
    dept_id=fetchDepartment();
    console.log(dept_id);
  });

  return (
    <div>Teaching</div>
  )
}
